mapper.select:
select notice_id, user_id, notice_title, notice_contents, notice_create_at, notice_delete_at from notices
where notice_title like concat('%', #{word}, '%') or notice_contents like concat('%', #{word}, '%')
order by notice_id desc limit #{start}, #{spp}

mapper.update:
update notices
set notice_title = #{noticeTitle}, notice_contents = #{noticeContents}
where notice_id = #{noticeId}
