mapper.select:
select tip_id, user_id, tip_title, tip_contents, tip_like_cnt, tip_create_at, tip_delete_at from tips
where tip_title like concat('%', #{word}, '%') or tip_contents like concat('%', #{word}, '%')
order by tip_id desc limit #{start}, #{spp}

mapper.update:
update tips
set tip_like_cnt = tip_like_cnt + 1
where tip_id = #{tipId}

update tips
set tip_title = #{tipTitle}, tip_contents = #{tipContents}
where tip_id = #{tipId}
